con:config.script:
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hi I am Durgaprasad"

// Context variable is used to access and modify the properties of your TestCase
//scope of context line in Testcase only

log.info context.expand ('${#TestCase#name}')
log.info context.expand ('${#TestCase#age}')

//To access / modify any property you have to come through its parent only
//Project-->testSuite-->testCase-->testStep

testRunner.testCase.testSuite.testCases["getEmployeeDetails"].setPropertyValue("id","789")
log.info testRunner.testCase.testSuite.testCases["getEmployeeDetails"].getPropertyValue("id")
log.info testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age")
log.info testRunner.testCase.getPropertyValue("Department")
testRunner.testCase.testSuite.testCases["getEmployeeDetails"].setPropertyValue("id","789")

def project=testRunner.testCase.testSuite.project
project.getPropertyValue("gender")

//Hit addEmployee Request with properties from TestCase Level
def addReq=testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].getPropertyValue("Request")
def name=testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("name")
def id=testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("id")
def age=testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age")

def xmlAdd= new XmlHolder(addReq)

xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department","cse")
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)

def newAddXml=xmlAdd.getXml();

testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].setPropertyValue("Request",newAddXml)

//Call the Service

// Context of AddEmployee TestCase should be Passed as add step
//Which we are trying to run belongs to the TestCase

def addTestStep=testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"]
def contextAddEmployee= new WsdlTestRunContext(addTestStep);
addTestStep.run(testRunner,contextAddEmployee)

// Hit getEmployee and generate response. Validate the response if it have the name which add in our earlier steps

def getReq=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].getPropertyValue("Request")
def getEmpXml= new XmlHolder(getReq)
getEmpXml.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)
def newGetEmpXml=getEmpXml.getXml()
testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].setPropertyValue("Request",newGetEmpXml)

def getTestStep= testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"];

def contextGetEmployee= new WsdlTestRunContext(getTestStep);
getTestStep.run(testRunner,contextGetEmployee)


//Validation


def getRes=testRunner.testCase.testSuite.testCases["getEmployeeDetails"].testSteps["get"].getPropertyValue("Response")

def getEmpRes= new XmlHolder(getRes)
def getResponse= getEmpRes.getNodeValue("//ns:name")
log.info getResponse

log.info name

assert getResponse==name


//Delete Employee Details


def deleteReq=testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"].getPropertyValue("Request")
def deleteEmpXml= new XmlHolder(deleteReq)
deleteEmpXml.setNodeValue("//typ:deleteEmployee/typ:employeeName",name)
def newDeleteEmpXml=deleteEmpXml.getXml()
testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"].setPropertyValue("Request",newDeleteEmpXml)

def deleteTestStep= testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"];

def contextDeleteEmployee= new WsdlTestRunContext(deleteTestStep);
deleteTestStep.run(testRunner,contextDeleteEmployee)


